mapper.select:
select bno, viewcnt, title, writer, content, replycnt
from sl_board
where bno=#{bno}

select fullName
from sl_attach
where bno = #{bno}

select email, password, chk, profilepath
  from sl_user
 where email = #{email} and password = #{password}

select *
  from sl_user
 where email = #{email} and password = #{password} and uuid = #{uuid}

mapper.update:
update sl_user
   set chk = #{chk}
 where email = #{email} and password = #{password} and uuid = #{uuid}

update sl_user
   set birthday = #{birthday}, password = #{password}, address = #{address}, postcode = #{postcode}, profilepath = #{profilepath} 
 where email = #{email}
